schemas:
DEFINE TABLE OVERWRITE has_experience_as
    SCHEMAFULL
    TYPE RELATION FROM participant | mentor TO specialization ENFORCED;

DEFINE FIELD OVERWRITE level ON has_experience_as
    TYPE int;
DEFINE TABLE OVERWRITE knows 
    SCHEMAFULL
    TYPE RELATION FROM participant | mentor TO technology ENFORCED;

DEFINE FIELD OVERWRITE level ON knows
    TYPE int;
DEFINE TABLE OVERWRITE member_of
    SCHEMAFULL
    TYPE RELATION FROM participant TO team ENFORCED;

DEFINE FIELD OVERWRITE specialization ON member_of
    TYPE record<specialization>;
DEFINE TABLE OVERWRITE mentor
    SCHEMAFULL;

DEFINE FIELD OVERWRITE username ON mentor 
    TYPE string;
DEFINE FIELD OVERWRITE password_hash ON mentor 
    TYPE string;
DEFINE FIELD OVERWRITE name ON mentor 
    TYPE string;
DEFINE FIELD OVERWRITE surname ON mentor 
    TYPE string;
DEFINE FIELD OVERWRITE bio ON mentor 
    TYPE string;
DEFINE TABLE OVERWRITE mentors
    SCHEMAFULL
    TYPE RELATION FROM mentor TO team ENFORCED;
DEFINE TABLE OVERWRITE organizator
    SCHEMAFULL;

DEFINE FIELD OVERWRITE password_hash ON organizator
    TYPE string;
DEFINE FIELD OVERWRITE username ON organizator
    TYPE string;

DEFINE INDEX OVERWRITE organizator_username ON organizator
    FIELDS username
    UNIQUE;

DEFINE TABLE OVERWRITE participant
    SCHEMAFULL;

DEFINE FIELD OVERWRITE bio ON participant
    TYPE string;
DEFINE FIELD OVERWRITE email ON participant
    TYPE string;
DEFINE FIELD OVERWRITE name ON participant
    TYPE string;
DEFINE FIELD OVERWRITE password_hash ON participant
    TYPE string;
DEFINE FIELD OVERWRITE portfolio_urls ON participant
    TYPE set<string>;
DEFINE FIELD OVERWRITE surname ON participant
    TYPE string;
DEFINE TABLE OVERWRITE participated_in
    SCHEMAFULL
    TYPE RELATION FROM participant | mentor TO tour ENFORCED;

DEFINE FIELD OVERWRITE score ON participated_in
    TYPE float;
DEFINE FIELD OVERWRITE specialization ON participated_in
    TYPE record<specialization>;
DEFINE FIELD OVERWRITE technologies ON participated_in
    TYPE set<record<technology>>;
DEFINE TABLE OVERWRITE reviewed
    SCHEMAFULL
    TYPE RELATION FROM participant | organizator | mentor TO participant | mentor ENFORCED;

DEFINE FIELD OVERWRITE review ON reviewed
    TYPE string;
DEFINE FIELD OVERWRITE score ON reviewed
    TYPE int;
DEFINE TABLE OVERWRITE script_migration SCHEMAFULL
    PERMISSIONS
        FOR select FULL
        FOR create, update, delete NONE;

DEFINE FIELD OVERWRITE script_name ON script_migration TYPE string;
DEFINE FIELD OVERWRITE executed_at ON script_migration TYPE datetime VALUE time::now() READONLY;
DEFINE TABLE OVERWRITE specialization
    SCHEMAFULL;
DEFINE TABLE OVERWRITE team
    SCHEMAFULL;

DEFINE FIELD OVERWRITE lead ON team
    TYPE record<participant>;
DEFINE FIELD OVERWRITE name ON team
    TYPE string;
DEFINE FIELD OVERWRITE tour ON team
    TYPE record<tour>;
DEFINE TABLE OVERWRITE technology
    SCHEMAFULL;
DEFINE TABLE OVERWRITE tour
    SCHEMAFULL;

DEFINE FIELD OVERWRITE ends_at ON tour
    TYPE datetime;
DEFINE FIELD OVERWRITE name ON tour
    TYPE string;
DEFINE FIELD OVERWRITE starts_at ON tour
    TYPE datetime;
DEFINE TABLE OVERWRITE uses
    SCHEMAFULL
    TYPE RELATION FROM team TO technology ENFORCED;
